package com.example.geogeusserclone.utils

import androidx.compose.ui.graphics.Color
import com.example.geogeusserclone.utils.Constants

enum class ScoreRating {
    PERFECT, EXCELLENT, GOOD, FAIR, POOR, TERRIBLE
}

object ScoreCalculator {

    /**
     * Berechnet den Score basierend auf Entfernung und Zeit
     */
    fun calculateScore(
        distanceKm: Double,
        timeSpentMs: Long = 0L,
        maxTimeMs: Long = Constants.MAX_ROUND_TIME_MS
    ): Int {
        // Basis-Score basierend auf Entfernung (0-5000 Punkte)
        val distanceScore = when {
            distanceKm <= Constants.PERFECT_DISTANCE_KM -> 5000
            distanceKm <= Constants.EXCELLENT_DISTANCE_KM -> 4500 - ((distanceKm - 1) * 50).toInt()
            distanceKm <= Constants.GOOD_DISTANCE_KM -> 4000 - ((distanceKm - 10) * 75).toInt()
            distanceKm <= Constants.FAIR_DISTANCE_KM -> 2000 - ((distanceKm - 50) * 10).toInt()
            distanceKm <= Constants.POOR_DISTANCE_KM -> 500 - ((distanceKm - 200) * 0.5).toInt()
            else -> 0
        }.coerceIn(0, 5000)

        // Zeit-Bonus (0-500 Punkte)
        val timeBonus = if (timeSpentMs > 0 && maxTimeMs > 0) {
            val timeRatio = (maxTimeMs - timeSpentMs).toDouble() / maxTimeMs
            (timeRatio * Constants.TIME_BONUS_MAX).toInt().coerceIn(0, Constants.TIME_BONUS_MAX)
        } else 0

        return (distanceScore + timeBonus).coerceIn(0, 5500)
    }

    /**
     * Bestimmt die Score-Bewertung
     */
    fun getScoreRating(score: Int): ScoreRating {
        return when {
            score >= 4500 -> ScoreRating.PERFECT
            score >= 3500 -> ScoreRating.EXCELLENT
            score >= 2500 -> ScoreRating.GOOD
            score >= 1000 -> ScoreRating.FAIR
            score >= 200 -> ScoreRating.POOR
            else -> ScoreRating.TERRIBLE
        }
    }

    /**
     * Gibt die passende Farbe fÃ¼r den Score zurÃ¼ck
     */
    fun getScoreColor(rating: ScoreRating): Color {
        return when (rating) {
            ScoreRating.PERFECT -> Color(0xFF4CAF50)      // GrÃ¼n
            ScoreRating.EXCELLENT -> Color(0xFF8BC34A)    // Hell-GrÃ¼n
            ScoreRating.GOOD -> Color(0xFFFFC107)         // Gelb
            ScoreRating.FAIR -> Color(0xFFFF9800)         // Orange
            ScoreRating.POOR -> Color(0xFFFF5722)         // Rot-Orange
            ScoreRating.TERRIBLE -> Color(0xFFF44336)     // Rot
        }
    }
}

package com.example.geogeusserclone.viewmodels

import androidx.lifecycle.ViewModel
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow

abstract class BaseViewModel<T>(initialState: T) : ViewModel() {

    private val _state = MutableStateFlow(initialState)
    val state: StateFlow<T> = _state.asStateFlow()

    protected fun setState(newState: T) {
        _state.value = newState
    }

    protected fun updateState(transform: (T) -> T) {
        _state.value = transform(_state.value)
    }
}

package com.example.geogeusserclone.viewmodels

import androidx.lifecycle.viewModelScope
import com.example.geogeusserclone.data.database.entities.GameEntity
import com.example.geogeusserclone.data.repositories.GameRepository
import com.example.geogeusserclone.data.repositories.GameStats
import com.example.geogeusserclone.data.repositories.UserRepository
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.collectLatest
import kotlinx.coroutines.launch
import javax.inject.Inject

data class StatsState(
    val isLoading: Boolean = false,
    val userGames: List<GameEntity> = emptyList(),
    val gameStats: GameStats? = null,
    val error: String? = null,
    val selectedTimeRange: TimeRange = TimeRange.ALL_TIME
)

enum class TimeRange {
    ALL_TIME, LAST_WEEK, LAST_MONTH, LAST_YEAR
}

@HiltViewModel
class StatsViewModel @Inject constructor(
    private val gameRepository: GameRepository,
    private val userRepository: UserRepository
) : BaseViewModel<StatsState>(StatsState()) {

    init {
        loadUserStats()
    }

    private fun loadUserStats() {
        viewModelScope.launch {
            val currentUser = userRepository.getCurrentUser()
            if (currentUser == null) {
                setState(state.value.copy(error = "Benutzer nicht angemeldet"))
                return@launch
            }

            setState(state.value.copy(isLoading = true))

            try {
                // Load user games
                gameRepository.getGamesByUser(currentUser.id).collectLatest { games ->
                    val filteredGames = filterGamesByTimeRange(games, state.value.selectedTimeRange)
                    setState(state.value.copy(userGames = filteredGames))
                }

                // Load game stats
                val stats = gameRepository.getUserStats(currentUser.id)
                setState(state.value.copy(
                    isLoading = false,
                    gameStats = stats,
                    error = null
                ))

            } catch (e: Exception) {
                setState(state.value.copy(
                    isLoading = false,
                    error = e.message ?: "Statistiken konnten nicht geladen werden"
                ))
            }
        }
    }

    fun changeTimeRange(timeRange: TimeRange) {
        setState(state.value.copy(selectedTimeRange = timeRange))
        loadUserStats()
    }

    private fun filterGamesByTimeRange(games: List<GameEntity>, timeRange: TimeRange): List<GameEntity> {
        val currentTime = System.currentTimeMillis()
        val filterTime = when (timeRange) {
            TimeRange.ALL_TIME -> 0L
            TimeRange.LAST_WEEK -> currentTime - (7 * 24 * 60 * 60 * 1000L)
            TimeRange.LAST_MONTH -> currentTime - (30 * 24 * 60 * 60 * 1000L)
            TimeRange.LAST_YEAR -> currentTime - (365 * 24 * 60 * 60 * 1000L)
        }

        return games.filter { it.timestamp >= filterTime }
    }

    fun getAverageScoreByGameMode(): Map<String, Double> {
        val games = state.value.userGames.filter { it.isCompleted }
        return games.groupBy { it.gameMode }
            .mapValues { (_, gameList) ->
                if (gameList.isEmpty()) 0.0
                else gameList.map { it.score }.average()
            }
    }

    fun getBestGamesByMode(): Map<String, GameEntity?> {
        val games = state.value.userGames.filter { it.isCompleted }
        return games.groupBy { it.gameMode }
            .mapValues { (_, gameList) ->
                gameList.maxByOrNull { it.score }
            }
    }

    fun getRecentGames(limit: Int = 10): List<GameEntity> {
        return state.value.userGames
            .filter { it.isCompleted }
            .sortedByDescending { it.timestamp }
            .take(limit)
    }

    fun clearError() {
        setState(state.value.copy(error = null))
    }
}

package com.example.geogeusserclone.viewmodels

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.geogeusserclone.data.database.entities.GameEntity
import com.example.geogeusserclone.data.models.GameStats
import com.example.geogeusserclone.data.models.UserStats
import com.example.geogeusserclone.data.repositories.GameRepository
import com.example.geogeusserclone.data.repositories.UserRepository
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.*
import kotlinx.coroutines.launch
import javax.inject.Inject

data class StatsUiState(
    val gameStats: GameStats = GameStats(),
    val userStats: List<UserStats> = emptyList(),
    val isLoading: Boolean = false,
    val error: String? = null
)

@HiltViewModel
class StatsViewModel @Inject constructor(
    private val gameRepository: GameRepository,
    private val userRepository: UserRepository
) : ViewModel() {

    private val _uiState = MutableStateFlow(StatsUiState())
    val uiState: StateFlow<StatsUiState> = _uiState.asStateFlow()

    init {
        loadUserStats()
    }

    private fun loadUserStats() {
        viewModelScope.launch {
            _uiState.update { it.copy(isLoading = true) }

            try {
                val currentUser = userRepository.getCurrentUser()
                if (currentUser != null) {
                    // Lade Spiele-Statistiken
                    gameRepository.getGameHistory(currentUser.id).collect { games: List<GameEntity> ->
                        val gameStats = calculateGameStats(games)
                        _uiState.update { 
                            it.copy(
                                gameStats = gameStats,
                                isLoading = false,
                                error = null
                            )
                        }
                    }
                } else {
                    _uiState.update { 
                        it.copy(
                            isLoading = false,
                            error = "Benutzer nicht angemeldet"
                        )
                    }
                }
            } catch (e: Exception) {
                _uiState.update { 
                    it.copy(
                        isLoading = false,
                        error = "Fehler beim Laden der Statistiken: ${e.message}"
                    )
                }
            }
        }
    }

    private fun calculateGameStats(games: List<GameEntity>): GameStats {
        if (games.isEmpty()) return GameStats()

        val completedGames = games.filter { it.isCompleted }
        val totalGames = completedGames.size
        val totalScore = completedGames.sumOf { it.score }
        val averageScore = if (totalGames > 0) totalScore.toDouble() / totalGames else 0.0
        val bestScore = completedGames.maxOfOrNull { it.score } ?: 0

        // Monatliche Statistiken berechnen
        val monthlyStats = completedGames
            .groupBy { game ->
                val date = java.util.Date(game.completedAt ?: game.createdAt)
                val calendar = java.util.Calendar.getInstance()
                calendar.time = date
                "${calendar.get(java.util.Calendar.MONTH + 1)}-${calendar.get(java.util.Calendar.YEAR)}"
            }
            .map { (monthYear, monthGames) ->
                val parts = monthYear.split("-")
                val month = parts[0]
                val year = parts[1].toInt()

                com.example.geogeusserclone.data.models.MonthlyGameStats(
                    month = getMonthName(month.toInt()),
                    year = year,
                    gamesPlayed = monthGames.size,
                    totalScore = monthGames.sumOf { it.score },
                    averageScore = monthGames.map { it.score }.average()
                )
            }
            .sortedByDescending { it.year * 12 + getMonthNumber(it.month) }

        return GameStats(
            totalGames = totalGames,
            totalScore = totalScore,
            averageScore = averageScore,
            bestScore = bestScore,
            monthlyStats = monthlyStats
        )
    }

    private fun getMonthName(month: Int): String {
        return when (month) {
            1 -> "Januar"
            2 -> "Februar"
            3 -> "MÃ¤rz"
            4 -> "April"
            5 -> "Mai"
            6 -> "Juni"
            7 -> "Juli"
            8 -> "August"
            9 -> "September"
            10 -> "Oktober"
            11 -> "November"
            12 -> "Dezember"
            else -> "Unbekannt"
        }
    }

    private fun getMonthNumber(monthName: String): Int {
        return when (monthName) {
            "Januar" -> 1
            "Februar" -> 2
            "MÃ¤rz" -> 3
            "April" -> 4
            "Mai" -> 5
            "Juni" -> 6
            "Juli" -> 7
            "August" -> 8
            "September" -> 9
            "Oktober" -> 10
            "November" -> 11
            "Dezember" -> 12
            else -> 1
        }
    }

    fun refreshStats() {
        loadUserStats()
    }

    fun clearError() {
        _uiState.update { it.copy(error = null) }
    }
}
