package com.example.geogeusserclone

import android.content.Context
import com.example.geogeusserclone.data.network.ApiService
import com.example.geogeusserclone.data.network.AuthInterceptor
import com.example.geogeusserclone.utils.Constants
import dagger.Module
import dagger.Provides
import dagger.hilt.InstallIn
import dagger.hilt.android.qualifiers.ApplicationContext
import dagger.hilt.components.SingletonComponent
import okhttp3.Cache
import okhttp3.OkHttpClient
import okhttp3.logging.HttpLoggingInterceptor
import retrofit2.Retrofit
import retrofit2.converter.gson.GsonConverterFactory
import javax.inject.Singleton
import java.util.concurrent.TimeUnit

@Module
@InstallIn(SingletonComponent::class)
object NetworkModule {

    @Provides
    @Singleton
    fun provideCache(@ApplicationContext context: Context): Cache {
        val cacheSize = 10 * 1024 * 1024L // 10 MB
        return Cache(context.cacheDir, cacheSize)
    }

    @Provides
    @Singleton
    fun provideAuthInterceptor(): AuthInterceptor {
        return AuthInterceptor()
    }

    @Provides
    @Singleton
    fun provideOkHttpClient(
        authInterceptor: AuthInterceptor,
        cache: Cache
    ): OkHttpClient {
        val loggingInterceptor = HttpLoggingInterceptor().apply {
            level = HttpLoggingInterceptor.Level.BODY
        }

        return OkHttpClient.Builder()
            .addInterceptor(authInterceptor)
            .addInterceptor(loggingInterceptor)
            .cache(cache)
            .connectTimeout(5, TimeUnit.SECONDS) // Reduziere Timeout
            .readTimeout(5, TimeUnit.SECONDS)
            .writeTimeout(5, TimeUnit.SECONDS)
            .build()
    }

    @Provides
    @Singleton
    fun provideRetrofit(okHttpClient: OkHttpClient): Retrofit {
        return Retrofit.Builder()
            .baseUrl(Constants.BASE_URL) // Directly use Constants.BASE_URL
            .client(okHttpClient)
            .addConverterFactory(GsonConverterFactory.create())
            .build()
    }

    @Provides
    @Singleton
    fun provideApiService(retrofit: Retrofit): ApiService {
        return retrofit.create(ApiService::class.java)
    }
}

package com.example.geogeusserclone

import android.content.Context
import com.example.geogeusserclone.data.network.ApiService
import com.example.geogeusserclone.data.network.AuthInterceptor
import com.example.geogeusserclone.utils.Constants
import dagger.Module
import dagger.Provides
import dagger.hilt.InstallIn
import dagger.hilt.android.qualifiers.ApplicationContext
import dagger.hilt.components.SingletonComponent
import okhttp3.Cache
import okhttp3.OkHttpClient
import okhttp3.logging.HttpLoggingInterceptor
import retrofit2.Retrofit
import retrofit2.converter.gson.GsonConverterFactory
import javax.inject.Singleton
import java.util.concurrent.TimeUnit

@Module
@InstallIn(SingletonComponent::class)
object NetworkModule {

    @Provides
    @Singleton
    fun provideCache(@ApplicationContext context: Context): Cache {
        val cacheSize = 10 * 1024 * 1024L // 10 MB
        return Cache(context.cacheDir, cacheSize)
    }

    @Provides
    @Singleton
    fun provideAuthInterceptor(): AuthInterceptor {
        return AuthInterceptor()
    }

    @Provides
    @Singleton
    fun provideOkHttpClient(
        authInterceptor: AuthInterceptor,
        cache: Cache
    ): OkHttpClient {
        val loggingInterceptor = HttpLoggingInterceptor().apply {
            level = HttpLoggingInterceptor.Level.BODY
        }

        return OkHttpClient.Builder()
            .addInterceptor(authInterceptor)
            .addInterceptor(loggingInterceptor)
            .cache(cache)
            .connectTimeout(Constants.CONNECT_TIMEOUT, TimeUnit.SECONDS)
            .readTimeout(Constants.READ_TIMEOUT, TimeUnit.SECONDS)
            .writeTimeout(Constants.WRITE_TIMEOUT, TimeUnit.SECONDS)
            .build()
    }

    @Provides
    @Singleton
    fun provideRetrofit(okHttpClient: OkHttpClient): Retrofit {
        return Retrofit.Builder()
            .baseUrl(Constants.BASE_URL)
            .client(okHttpClient)
            .addConverterFactory(GsonConverterFactory.create())
            .build()
    }

    @Provides
    @Singleton
    fun provideApiService(retrofit: Retrofit): ApiService {
        return retrofit.create(ApiService::class.java)
    }
}

package com.example.geogeusserclone

import com.example.geogeusserclone.data.database.AppDatabase
import com.example.geogeusserclone.data.network.ApiService
import com.example.geogeusserclone.data.network.AuthInterceptor
import com.example.geogeusserclone.data.repositories.*
import dagger.Module
import dagger.Provides
import dagger.hilt.InstallIn
import dagger.hilt.components.SingletonComponent
import javax.inject.Singleton

@Module
@InstallIn(SingletonComponent::class)
object RepositoryModule {

    @Provides
    @Singleton
    fun provideUserRepository(
        apiService: ApiService,
        database: AppDatabase,
        authInterceptor: AuthInterceptor
    ): UserRepository {
        return UserRepository(
            apiService = apiService,
            userDao = database.userDao(),
            authInterceptor = authInterceptor
        )
    }

    @Provides
    @Singleton
    fun provideLocationRepository(
        apiService: ApiService,
        database: AppDatabase
    ): LocationRepository {
        return LocationRepository(
            apiService = apiService,
            locationDao = database.locationDao()
        )
    }

    @Provides
    @Singleton
    fun provideGameRepository(
        apiService: ApiService,
        database: AppDatabase,
        userRepository: UserRepository
    ): GameRepository {
        return GameRepository(
            apiService = apiService,
            gameDao = database.gameDao(),
            guessDao = database.guessDao(),
            userRepository = userRepository
        )
    }

    @Provides
    @Singleton
    fun provideLocationCacheRepository(
        apiService: ApiService,
        database: AppDatabase
    ): LocationCacheRepository {
        return LocationCacheRepository(
            apiService = apiService,
            locationDao = database.locationDao()
        )
    }
}

package com.example.geogeusserclone.data.database

import androidx.room.Database
import androidx.room.Room
import androidx.room.RoomDatabase
import androidx.room.TypeConverters
import com.example.geogeusserclone.data.database.dao.*
import com.example.geogeusserclone.data.database.entities.*

@Database(
    entities = [
        UserEntity::class,
        LocationEntity::class,
        GameEntity::class,
        GuessEntity::class
    ],
    version = 4,
    exportSchema = false
)
@TypeConverters(Converters::class)
abstract class AppDatabase : RoomDatabase() {

    abstract fun userDao(): UserDao
    abstract fun locationDao(): LocationDao
    abstract fun gameDao(): GameDao
    abstract fun guessDao(): GuessDao
}

// Type Converters for Room
class Converters {
    // Room braucht diese für komplexe Datentypen, falls nötig
}

package com.example.geogeusserclone.data.database
import androidx.room.Database import androidx.room.Room import androidx.room.RoomDatabase import com.example.geogeusserclone.data.database.dao.* import com.example.geogeusserclone.data.database.entities.*
@Database( entities = [ UserEntity::class, LocationEntity::class, GameEntity::class, GuessEntity::class ], version = 4, exportSchema = false ) abstract class AppDatabase : RoomDatabase() {
abstract fun userDao(): UserDao abstract fun locationDao(): LocationDao abstract fun gameDao(): GameDao abstract fun guessDao(): GuessDao }

package com.example.geogeusserclone.data.database.dao

import androidx.room.*
import com.example.geogeusserclone.data.database.entities.GameEntity
import kotlinx.coroutines.flow.Flow

@Dao
interface GameDao {

    @Query("SELECT * FROM games WHERE id = :gameId")
    suspend fun getGameById(gameId: String): GameEntity?

    @Query("SELECT * FROM games WHERE userId = :userId ORDER BY createdAt DESC")
    fun getGamesByUser(userId: String): Flow<List<GameEntity>>

    @Query("SELECT * FROM games WHERE userId = :userId AND isCompleted = 0 LIMIT 1")
    suspend fun getCurrentGameForUser(userId: String): GameEntity?

    @Query("SELECT * FROM games WHERE userId = :userId AND isCompleted = 1 ORDER BY score DESC LIMIT 1")
    suspend fun getBestGameForUser(userId: String): GameEntity?

    @Query("SELECT * FROM games ORDER BY createdAt DESC LIMIT :limit")
    suspend fun getRecentGames(limit: Int = 10): List<GameEntity>

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertGame(game: GameEntity)

    @Update
    suspend fun updateGame(game: GameEntity)

    @Delete
    suspend fun deleteGame(game: GameEntity)

    @Query("DELETE FROM games WHERE userId = :userId")
    suspend fun deleteGamesByUser(userId: String)

    @Query("SELECT COUNT(*) FROM games WHERE userId = :userId")
    suspend fun getGameCountByUser(userId: String): Int

    @Query("SELECT COUNT(*) FROM games WHERE userId = :userId AND isCompleted = 1")
    suspend fun getCompletedGameCountByUser(userId: String): Int
}

