package com.example.geogeusserclone.data.network

import okhttp3.Interceptor
import okhttp3.Response
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class AuthInterceptor @Inject constructor() : Interceptor {

    private var authToken: String? = null

    fun setAuthToken(token: String?) {
        authToken = token
    }

    override fun intercept(chain: Interceptor.Chain): Response {
        val originalRequest = chain.request()

        val requestBuilder = originalRequest.newBuilder()
            .addHeader("Content-Type", "application/json")
            .addHeader("Accept", "application/json")

        authToken?.let { token ->
            requestBuilder.addHeader("Authorization", "Bearer $token")
        }

        return chain.proceed(requestBuilder.build())
    }
}

package com.example.geogeusserclone.data.network

sealed class NetworkResult<T> {
    data class Success<T>(val data: T) : NetworkResult<T>()
    data class Error<T>(val message: String, val code: Int? = null) : NetworkResult<T>()
    data class Exception<T>(val e: Throwable) : NetworkResult<T>()
    class Loading<T> : NetworkResult<T>()
}

package com.example.geogeusserclone.data.repositories

abstract class BaseRepository {

    protected suspend fun <T> safeApiCall(
        apiCall: suspend () -> T
    ): Result<T> {
        return try {
            Result.success(apiCall())
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    protected fun <T> handleApiResponse(
        response: retrofit2.Response<T>
    ): Result<T> {
        return if (response.isSuccessful && response.body() != null) {
            Result.success(response.body()!!)
        } else {
            Result.failure(Exception("API Error: ${response.code()} ${response.message()}"))
        }
    }
}

package com.example.geogeusserclone.data.repositories

import com.example.geogeusserclone.data.database.dao.GameDao
import com.example.geogeusserclone.data.database.dao.GuessDao
import com.example.geogeusserclone.data.database.entities.GameEntity
import com.example.geogeusserclone.data.database.entities.GuessEntity
import com.example.geogeusserclone.data.network.ApiService
import com.example.geogeusserclone.data.network.CreateGameRequest
import com.example.geogeusserclone.data.network.CreateSinglePlayerGameRequest
import com.example.geogeusserclone.data.network.GuessRequest
import com.example.geogeusserclone.data.network.GameResultRequest
import com.example.geogeusserclone.data.network.GuessResultData
import com.example.geogeusserclone.utils.DistanceCalculator
import com.example.geogeusserclone.utils.ScoreCalculator
import kotlinx.coroutines.flow.Flow
import java.util.UUID
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class GameRepository @Inject constructor(
    private val apiService: ApiService,
    private val gameDao: GameDao,
    private val guessDao: GuessDao,
    private val userRepository: UserRepository
) : BaseRepository() {

    suspend fun createGame(
        userId: String,
        gameMode: String = "single",
        rounds: Int = 5
    ): Result<GameEntity> {
        return try {
            // Verwende neuen Single Player Endpoint
            val response = apiService.createSinglePlayerGame(
                CreateSinglePlayerGameRequest(rounds, gameMode)
            )

            if (response.isSuccessful) {
                val gameResponse = response.body()!!
                val gameEntity = GameEntity(
                    id = gameResponse.id,
                    userId = userId,
                    gameMode = gameMode,
                    totalRounds = rounds,
                    currentRound = 1,
                    score = 0,
                    isCompleted = false,
                    createdAt = System.currentTimeMillis(),
                    startedAt = System.currentTimeMillis()
                )
                gameDao.insertGame(gameEntity)
                Result.success(gameEntity)
            } else {
                createOfflineGame(userId, gameMode, rounds)
            }
        } catch (e: Exception) {
            // Fallback für Offline-Modus
            createOfflineGame(userId, gameMode, rounds)
        }
    }

    private suspend fun createOfflineGame(
        userId: String,
        gameMode: String,
        rounds: Int
    ): Result<GameEntity> {
        val gameEntity = GameEntity(
            id = UUID.randomUUID().toString(),
            userId = userId,
            gameMode = gameMode,
            totalRounds = rounds,
            currentRound = 1,
            score = 0,
            isCompleted = false,
            createdAt = System.currentTimeMillis(),
            startedAt = System.currentTimeMillis()
        )
        gameDao.insertGame(gameEntity)
        return Result.success(gameEntity)
    }

    suspend fun submitGuess(
        gameId: String,
        locationId: String,
        guessLat: Double,
        guessLng: Double,
        actualLat: Double,
        actualLng: Double,
        timeSpent: Long
    ): Result<GuessEntity> {
        return try {
            // Berechne Score und Distanz lokal
            val distance = DistanceCalculator.calculateDistance(
                guessLat, guessLng, actualLat, actualLng
            )
            val score = ScoreCalculator.calculateScore(distance, timeSpent)

            val guessEntity = GuessEntity(
                id = UUID.randomUUID().toString(),
                gameId = gameId,
                locationId = locationId,
                guessLat = guessLat,
                guessLng = guessLng,
                actualLat = actualLat,
                actualLng = actualLng,
                distance = distance,
                score = score,
                timeSpent = timeSpent,
                submittedAt = System.currentTimeMillis()
            )

            // Speichere Guess lokal
            guessDao.insertGuess(guessEntity)

            // Update Game Score
            val currentGame = gameDao.getGameById(gameId)
            if (currentGame != null) {
                val updatedGame = currentGame.copy(
                    score = currentGame.score + score,
                    currentRound = currentGame.currentRound + 1
                )
                gameDao.updateGame(updatedGame)
            }

            // Versuche online zu synchronisieren
            try {
                apiService.submitGuess(
                    gameId,
                    GuessRequest(locationId, guessLat, guessLng, timeSpent)
                )
            } catch (e: Exception) {
                // Silent fail - Guess ist bereits lokal gespeichert
            }

            Result.success(guessEntity)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    suspend fun completeGame(gameId: String): Result<GameEntity> {
        return try {
            val game = gameDao.getGameById(gameId)
            if (game != null) {
                val completedGame = game.copy(
                    isCompleted = true,
                    completedAt = System.currentTimeMillis(),
                    duration = System.currentTimeMillis() - (game.startedAt ?: game.createdAt)
                )
                gameDao.updateGame(completedGame)

                // Update User Stats
                userRepository.updateUserStats(
                    totalScore = completedGame.score,
                    gamesPlayed = 1,
                    bestScore = completedGame.score
                )

                Result.success(completedGame)
            } else {
                Result.failure(Exception("Spiel nicht gefunden"))
            }
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    // Neue Methode für komplettes Spiel-Ergebnis
    suspend fun submitCompleteGameResult(
        game: GameEntity,
        guesses: List<GuessEntity>
    ): Result<GameResultResponse> {
        return try {
            val gameResultRequest = GameResultRequest(
                guesses = guesses.map { guess ->
                    GuessResultData(
                        locationId = guess.locationId,
                        guessLat = guess.guessLat,
                        guessLng = guess.guessLng,
                        actualLat = guess.actualLat,
                        actualLng = guess.actualLng,
                        distance = guess.distance,
                        score = guess.score,
                        timeSpent = guess.timeSpent
                    )
                },
                totalScore = game.score,
                completedAt = System.currentTimeMillis()
            )

            val response = apiService.submitGameResult(game.id, gameResultRequest)

            if (response.isSuccessful) {
                val result = response.body()!!
                Result.success(result)
            } else {
                Result.failure(Exception("Fehler beim Übermitteln des Spielergebnisses"))
            }
        } catch (e: Exception) {
            // Silent fail - Spiel ist lokal gespeichert
            Result.failure(e)
        }
    }

    fun getGameHistory(userId: String): Flow<List<GameEntity>> {
        return gameDao.getGamesByUser(userId)
    }

    fun getGuessesByGame(gameId: String): Flow<List<GuessEntity>> {
        return guessDao.getGuessesByGame(gameId)
    }

    suspend fun getCurrentGame(userId: String): GameEntity? {
        return gameDao.getCurrentGameForUser(userId)
    }
}

package com.example.geogeusserclone.data.repositories

import com.example.geogeusserclone.data.database.dao.GameDao
import com.example.geogeusserclone.data.database.dao.GuessDao
import com.example.geogeusserclone.data.database.entities.GameEntity
import com.example.geogeusserclone.data.database.entities.GuessEntity
import com.example.geogeusserclone.data.network.ApiService
import com.example.geogeusserclone.data.network.CreateGameRequest
import com.example.geogeusserclone.data.network.CreateSinglePlayerGameRequest
import com.example.geogeusserclone.data.network.GuessRequest
import com.example.geogeusserclone.data.network.GameResultRequest
import com.example.geogeusserclone.data.network.GuessResultData
import com.example.geogeusserclone.data.network.GameResultResponse
import com.example.geogeusserclone.utils.DistanceCalculator
import com.example.geogeusserclone.utils.ScoreCalculator
import kotlinx.coroutines.flow.Flow
import java.util.UUID
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class GameRepository @Inject constructor(
    private val apiService: ApiService,
    private val gameDao: GameDao,
    private val guessDao: GuessDao,
    private val userRepository: UserRepository
) : BaseRepository() {

    suspend fun createGame(
        userId: String,
        gameMode: String = "single",
        rounds: Int = 5
    ): Result<GameEntity> {
        return try {
            // Verwende neuen Single Player Endpoint
            val response = apiService.createSinglePlayerGame(
                CreateSinglePlayerGameRequest(rounds, gameMode)
            )

            if (response.isSuccessful) {
                val gameResponse = response.body()!!
                val gameEntity = GameEntity(
                    id = gameResponse.id,
                    userId = userId,
                    gameMode = gameMode,
                    totalRounds = rounds,
                    currentRound = 1,
                    score = 0,
                    isCompleted = false,
                    createdAt = System.currentTimeMillis(),
                    startedAt = System.currentTimeMillis()
                )
                gameDao.insertGame(gameEntity)
                Result.success(gameEntity)
            } else {
                createOfflineGame(userId, gameMode, rounds)
            }
        } catch (e: Exception) {
            // Fallback für Offline-Modus
            createOfflineGame(userId, gameMode, rounds)
        }
    }

    private suspend fun createOfflineGame(
        userId: String,
        gameMode: String,
        rounds: Int
    ): Result<GameEntity> {
        val gameEntity = GameEntity(
            id = UUID.randomUUID().toString(),
            userId = userId,
            gameMode = gameMode,
            totalRounds = rounds,
            currentRound = 1,
            score = 0,
            isCompleted = false,
            createdAt = System.currentTimeMillis(),
            startedAt = System.currentTimeMillis()
        )
        gameDao.insertGame(gameEntity)
        return Result.success(gameEntity)
    }

    suspend fun submitGuess(
        gameId: String,
        locationId: String,
        guessLat: Double,
        guessLng: Double,
        actualLat: Double,
        actualLng: Double,
        timeSpent: Long
    ): Result<GuessEntity> {
        return try {
            // Berechne Score und Distanz lokal
            val distance = DistanceCalculator.calculateDistance(
                guessLat, guessLng, actualLat, actualLng
            )
            val score = ScoreCalculator.calculateScore(distance, timeSpent)

            val guessEntity = GuessEntity(
                id = UUID.randomUUID().toString(),
                gameId = gameId,
                locationId = locationId,
                guessLat = guessLat,
                guessLng = guessLng,
                actualLat = actualLat,
                actualLng = actualLng,
                distance = distance,
                score = score,
                timeSpent = timeSpent,
                submittedAt = System.currentTimeMillis()
            )

            // Speichere Guess lokal
            guessDao.insertGuess(guessEntity)

            // Update Game Score
            val currentGame = gameDao.getGameById(gameId)
            if (currentGame != null) {
                val updatedGame = currentGame.copy(
                    score = currentGame.score + score,
                    currentRound = currentGame.currentRound + 1
                )
                gameDao.updateGame(updatedGame)
            }

            // Versuche online zu synchronisieren - remove this part that causes error
            // The backend doesn't have this endpoint, so we skip it

            Result.success(guessEntity)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    suspend fun completeGame(gameId: String): Result<GameEntity> {
        return try {
            val game = gameDao.getGameById(gameId)
            if (game != null) {
                val completedGame = game.copy(
                    isCompleted = true,
                    completedAt = System.currentTimeMillis(),
                    duration = System.currentTimeMillis() - (game.startedAt ?: game.createdAt)
                )
                gameDao.updateGame(completedGame)

                // Update User Stats
                userRepository.updateUserStats(
                    totalScore = completedGame.score,
                    gamesPlayed = 1,
                    bestScore = completedGame.score
                )

                Result.success(completedGame)
            } else {
                Result.failure(Exception("Spiel nicht gefunden"))
            }
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    // Neue Methode für komplettes Spiel-Ergebnis
    suspend fun submitCompleteGameResult(
        game: GameEntity,
        guesses: List<GuessEntity>
    ): Result<GameResultResponse> {
        return try {
            val gameResultRequest = GameResultRequest(
                guesses = guesses.map { guess ->
                    GuessResultData(
                        locationId = guess.locationId,
                        guessLat = guess.guessLat,
                        guessLng = guess.guessLng,
                        actualLat = guess.actualLat,
                        actualLng = guess.actualLng,
                        distance = guess.distance,
                        score = guess.score,
                        timeSpent = guess.timeSpent
                    )
                },
                totalScore = game.score,
                completedAt = System.currentTimeMillis()
            )

            val response = apiService.submitGameResult(game.id, gameResultRequest)

            if (response.isSuccessful) {
                val result = response.body()!!
                Result.success(result)
            } else {
                Result.failure(Exception("Fehler beim Übermitteln des Spielergebnisses"))
            }
        } catch (e: Exception) {
            // Silent fail - Spiel ist lokal gespeichert
            Result.failure(e)
        }
    }

    fun getGameHistory(userId: String): Flow<List<GameEntity>> {
        return gameDao.getGamesByUser(userId)
    }

    fun getGuessesByGame(gameId: String): Flow<List<GuessEntity>> {
        return guessDao.getGuessesByGame(gameId)
    }

    suspend fun getCurrentGame(userId: String): GameEntity? {
        return gameDao.getCurrentGameForUser(userId)
    }
}

package com.example.geogeusserclone.data.repositories

import com.example.geogeusserclone.data.database.dao.LocationDao
import com.example.geogeusserclone.data.database.entities.LocationEntity
import com.example.geogeusserclone.data.network.ApiService
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.withContext
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class LocationCacheRepository @Inject constructor(
    private val apiService: ApiService,
    private val locationDao: LocationDao
) {

    suspend fun preloadLocationsInBackground() = withContext(Dispatchers.IO) {
        try {
            // Lade nur wenn weniger als 10 Locations im Cache
            val cachedCount = locationDao.getCachedLocationCount()
            if (cachedCount < 10) {
                val response = apiService.getLocations(50)
                if (response.isSuccessful) {
                    val locationsResponse = response.body()!!
                    val locationEntities = locationsResponse.locations.map { location ->
                        LocationEntity(
                            id = location.id,
                            latitude = location.latitude,
                            longitude = location.longitude,
                            imageUrl = location.imageUrl,
                            country = location.country,
                            city = location.city,
                            difficulty = location.difficulty,
                            isCached = true,
                            isUsed = false
                        )
                    }

                    // Batch insert für bessere Performance
                    locationDao.insertLocations(locationEntities)

                    // Preload images im Hintergrund
                    preloadImages(locationEntities.take(5)) // Nur erste 5 Bilder
                }
            }
        } catch (e: Exception) {
            // Silent fail - App funktioniert weiter mit Fallback Locations
        }
    }

    private suspend fun preloadImages(locations: List<LocationEntity>) = withContext(Dispatchers.IO) {
        // Implementierung für Image Preloading
        // Dies würde die Bilder in den Cache laden ohne sie anzuzeigen
    }

    suspend fun getNextLocations(count: Int = 3): List<LocationEntity> = withContext(Dispatchers.IO) {
        locationDao.getUnusedLocations(count)
    }
}

package com.example.geogeusserclone.data.repositories

import com.example.geogeusserclone.data.database.dao.LocationDao
import com.example.geogeusserclone.data.database.entities.LocationEntity
import com.example.geogeusserclone.data.network.ApiService
import com.example.geogeusserclone.data.network.NetworkResult
import kotlinx.coroutines.flow.Flow
import java.util.UUID
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class LocationRepository @Inject constructor(
    private val apiService: ApiService,
    private val locationDao: LocationDao
) : BaseRepository() {

    fun getCachedLocations(): Flow<List<LocationEntity>> = locationDao.getCachedLocations()

    suspend fun getRandomLocation(): Result<LocationEntity> {
        return try {
            // Versuche zuerst eine unbenutzte lokale Location zu finden
            val unusedLocation = locationDao.getRandomUnusedLocation()
            if (unusedLocation != null) {
                locationDao.markLocationAsUsed(unusedLocation.id)
                return Result.success(unusedLocation)
            }

            // Falls keine lokalen Locations, erstelle Fallback-Locations
            val fallbackLocations = createFallbackLocations()
            if (fallbackLocations.isNotEmpty()) {
                locationDao.insertLocations(fallbackLocations)
                val randomLocation = fallbackLocations.random()
                locationDao.markLocationAsUsed(randomLocation.id)
                return Result.success(randomLocation)
            }

            // Versuche online
            val response = apiService.getRandomLocation()
            if (response.isSuccessful) {
                val locationResponse = response.body()!!
                val locationEntity = LocationEntity(
                    id = locationResponse.id,
                    latitude = locationResponse.latitude,
                    longitude = locationResponse.longitude,
                    imageUrl = locationResponse.imageUrl,
                    country = locationResponse.country,
                    city = locationResponse.city,
                    difficulty = locationResponse.difficulty,
                    isCached = false,
                    isUsed = true
                )

                locationDao.insertLocation(locationEntity)
                Result.success(locationEntity)
            } else {
                Result.failure(Exception("Keine Locations verfügbar"))
            }
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    private fun createFallbackLocations(): List<LocationEntity> {
        return listOf(
            LocationEntity(
                id = "fallback_1",
                latitude = 48.8566,
                longitude = 2.3522,
                imageUrl = "https://images.unsplash.com/photo-1502602898536-47ad22581b52",
                country = "France",
                city = "Paris",
                difficulty = 2,
                isCached = true,
                isUsed = false
            ),
            LocationEntity(
                id = "fallback_2",
                latitude = 51.5074,
                longitude = -0.1278,
                imageUrl = "https://images.unsplash.com/photo-1513635269975-59663e0ac1ad",
                country = "United Kingdom",
                city = "London",
                difficulty = 2,
                isCached = true,
                isUsed = false
            ),
            LocationEntity(
                id = "fallback_3",
                latitude = 40.7128,
                longitude = -74.0060,
                imageUrl = "https://images.unsplash.com/photo-1496442226666-8d4d0e62e6e9",
                country = "United States",
                city = "New York",
                difficulty = 3,
                isCached = true,
                isUsed = false
            )
        )
    }

    suspend fun preloadLocations() {
        try {
            val response = apiService.getLocations(50)
            if (response.isSuccessful) {
                val locationsResponse = response.body()!!
                val locationEntities = locationsResponse.locations.map { location ->
                    LocationEntity(
                        id = location.id,
                        latitude = location.latitude,
                        longitude = location.longitude,
                        imageUrl = location.imageUrl,
                        country = location.country,
                        city = location.city,
                        difficulty = location.difficulty,
                        isCached = true,
                        isUsed = false
                    )
                }
                locationDao.insertLocations(locationEntities)
            }
        } catch (e: Exception) {
            // Ignoriere Netzwerkfehler beim Preloading
        }
    }

    suspend fun resetLocationUsage() {
        locationDao.resetAllLocationsUsage()
    }

    suspend fun getCachedLocationCount(): Int = locationDao.getCachedLocationCount()

    suspend fun testBackendConnection(): Result<Boolean> {
        return try {
            val response = apiService.getLocations(1) // Teste mit nur einer Location
            if (response.isSuccessful) {
                Result.success(true)
            } else {
                Result.failure(Exception("Backend nicht erreichbar: ${response.code()}"))
            }
        } catch (e: Exception) {
            Result.failure(Exception("Netzwerkfehler: ${e.message}"))
        }
    }
}

package com.example.geogeusserclone.data.repositories

import com.example.geogeusserclone.data.database.dao.LocationDao
import com.example.geogeusserclone.data.database.entities.LocationEntity
import com.example.geogeusserclone.data.network.ApiService
import com.example.geogeusserclone.data.network.NetworkResult
import kotlinx.coroutines.flow.Flow
import java.util.UUID
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class LocationRepository @Inject constructor(
    private val apiService: ApiService,
    private val locationDao: LocationDao
) : BaseRepository() {

    fun getCachedLocations(): Flow<List<LocationEntity>> = locationDao.getCachedLocations()

    suspend fun getRandomLocation(): Result<LocationEntity> {
        return try {
            // Versuche zuerst eine unbenutzte lokale Location zu finden
            val unusedLocation = locationDao.getRandomUnusedLocation()
            if (unusedLocation != null) {
                locationDao.markLocationAsUsed(unusedLocation.id)
                return Result.success(unusedLocation)
            }

            // Falls keine lokalen Locations, versuche Backend
            val response = apiService.getRandomLocation(count = 1)
            if (response.isSuccessful) {
                val locationsResponse = response.body()!!
                if (locationsResponse.locations.isNotEmpty()) {
                    val locationResponse = locationsResponse.locations.first()
                    val locationEntity = LocationEntity(
                        id = locationResponse.id,
                        latitude = locationResponse.latitude,
                        longitude = locationResponse.longitude,
                        imageUrl = locationResponse.imageUrl,
                        country = locationResponse.country,
                        city = locationResponse.city,
                        difficulty = locationResponse.difficulty,
                        isCached = false,
                        isUsed = true
                    )

                    locationDao.insertLocation(locationEntity)
                    return Result.success(locationEntity)
                }
            }

            // Fallback auf lokale Fallback-Locations
            val fallbackLocations = createFallbackLocations()
            if (fallbackLocations.isNotEmpty()) {
                locationDao.insertLocations(fallbackLocations)
                val randomLocation = fallbackLocations.random()
                locationDao.markLocationAsUsed(randomLocation.id)
                return Result.success(randomLocation)
            }

            Result.failure(Exception("Keine Locations verfügbar"))
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    private fun createFallbackLocations(): List<LocationEntity> {
        return listOf(
            LocationEntity(
                id = "fallback_1",
                latitude = 48.8566,
                longitude = 2.3522,
                imageUrl = "https://images.unsplash.com/photo-1502602898536-47ad22581b52",
                country = "France",
                city = "Paris",
                difficulty = 2,
                isCached = true,
                isUsed = false
            ),
            LocationEntity(
                id = "fallback_2",
                latitude = 51.5074,
                longitude = -0.1278,
                imageUrl = "https://images.unsplash.com/photo-1513635269975-59663e0ac1ad",
                country = "United Kingdom",
                city = "London",
                difficulty = 2,
                isCached = true,
                isUsed = false
            ),
            LocationEntity(
                id = "fallback_3",
                latitude = 40.7128,
                longitude = -74.0060,
                imageUrl = "https://images.unsplash.com/photo-1496442226666-8d4d0e62e6e9",
                country = "United States",
                city = "New York",
                difficulty = 3,
                isCached = true,
                isUsed = false
            )
        )
    }

    suspend fun preloadLocations() {
        try {
            val response = apiService.getLocations(50)
            if (response.isSuccessful) {
                val locationsResponse = response.body()!!
                val locationEntities = locationsResponse.locations.map { location ->
                    LocationEntity(
                        id = location.id,
                        latitude = location.latitude,
                        longitude = location.longitude,
                        imageUrl = location.imageUrl,
                        country = location.country,
                        city = location.city,
                        difficulty = location.difficulty,
                        isCached = true,
                        isUsed = false
                    )
                }
                locationDao.insertLocations(locationEntities)
            }
        } catch (e: Exception) {
            // Ignoriere Netzwerkfehler beim Preloading
        }
    }

    suspend fun resetLocationUsage() {
        locationDao.resetAllLocationsUsage()
    }

    suspend fun getCachedLocationCount(): Int = locationDao.getCachedLocationCount()

    suspend fun testBackendConnection(): Result<Boolean> {
        return try {
            val response = apiService.getLocations(1) // Teste mit nur einer Location
            if (response.isSuccessful) {
                Result.success(true)
            } else {
                Result.failure(Exception("Backend nicht erreichbar: ${response.code()}"))
            }
        } catch (e: Exception) {
            Result.failure(Exception("Netzwerkfehler: ${e.message}"))
        }
    }
}

package com.example.geogeusserclone.data.repositories

import com.example.geogeusserclone.data.database.dao.UserDao
import com.example.geogeusserclone.data.database.entities.UserEntity
import com.example.geogeusserclone.data.network.ApiService
import com.example.geogeusserclone.data.network.AuthInterceptor
import com.example.geogeusserclone.data.network.LoginRequest
import com.example.geogeusserclone.data.network.RegisterRequest
import kotlinx.coroutines.flow.Flow
import java.util.UUID
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class UserRepository @Inject constructor(
    private val apiService: ApiService,
    private val userDao: UserDao,
    private val authInterceptor: AuthInterceptor
) : BaseRepository() {

    suspend fun getCurrentUser(): UserEntity? {
        return userDao.getCurrentUser()
    }

    suspend fun login(email: String, password: String): Result<UserEntity> {
        return try {
            val response = apiService.login(LoginRequest(email, password))
            if (response.isSuccessful) {
                val loginResponse = response.body()!!

                // Setze Auth Token
                authInterceptor.setAuthToken(loginResponse.token)

                val userEntity = UserEntity(
                    id = loginResponse.user.id,
                    username = loginResponse.user.username,
                    email = loginResponse.user.email,
                    authToken = loginResponse.token,
                    totalScore = loginResponse.user.totalScore,
                    gamesPlayed = loginResponse.user.gamesPlayed,
                    bestScore = loginResponse.user.bestScore,
                    lastLoginAt = System.currentTimeMillis(),
                    createdAt = System.currentTimeMillis()
                )

                userDao.insertUser(userEntity)
                Result.success(userEntity)
            } else {
                Result.failure(Exception("Login fehlgeschlagen: ${response.message()}"))
            }
        } catch (e: Exception) {
            // Fallback für Offline-Modus
            val offlineUser = createOfflineUser(email)
            userDao.insertUser(offlineUser)
            Result.success(offlineUser)
        }
    }

    suspend fun register(username: String, email: String, password: String): Result<UserEntity> {
        return try {
            val response = apiService.register(RegisterRequest(username, email, password))
            if (response.isSuccessful) {
                val loginResponse = response.body()!!

                authInterceptor.setAuthToken(loginResponse.token)

                val userEntity = UserEntity(
                    id = loginResponse.user.id,
                    username = loginResponse.user.username,
                    email = loginResponse.user.email,
                    authToken = loginResponse.token,
                    totalScore = 0,
                    gamesPlayed = 0,
                    bestScore = 0,
                    lastLoginAt = System.currentTimeMillis(),
                    createdAt = System.currentTimeMillis()
                )

                userDao.insertUser(userEntity)
                Result.success(userEntity)
            } else {
                Result.failure(Exception("Registrierung fehlgeschlagen: ${response.message()}"))
            }
        } catch (e: Exception) {
            // Fallback für Offline-Modus
            val offlineUser = createOfflineUser(email, username)
            userDao.insertUser(offlineUser)
            Result.success(offlineUser)
        }
    }

    suspend fun logout() {
        authInterceptor.setAuthToken(null)
        userDao.clearCurrentUser()
    }

    suspend fun updateUserStats(totalScore: Int, gamesPlayed: Int, bestScore: Int) {
        val currentUser = userDao.getCurrentUser()
        currentUser?.let { user ->
            val updatedUser = user.copy(
                totalScore = user.totalScore + totalScore,
                gamesPlayed = user.gamesPlayed + gamesPlayed,
                bestScore = maxOf(user.bestScore, bestScore),
                lastLoginAt = System.currentTimeMillis()
            )
            userDao.updateUser(updatedUser)
        }
    }

    private fun createOfflineUser(email: String, username: String = "Offline User"): UserEntity {
        return UserEntity(
            id = UUID.randomUUID().toString(),
            username = username,
            email = email,
            authToken = null,
            totalScore = 0,
            gamesPlayed = 0,
            bestScore = 0,
            lastLoginAt = System.currentTimeMillis(),
            createdAt = System.currentTimeMillis()
        )
    }

    fun getAllUsers(): Flow<List<UserEntity>> {
        return userDao.getAllUsers()
    }
}

package com.example.geogeusserclone.ui.components

import androidx.compose.foundation.layout.*
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import com.example.geogeusserclone.data.database.entities.GuessEntity

@Composable
fun RoundResultView(
    guess: GuessEntity?,
    onNextRound: () -> Unit,
    modifier: Modifier = Modifier
) {
    guess?.let { guessEntity ->
        Card(
            modifier = modifier
                .fillMaxSize()
                .padding(16.dp)
        ) {
            Column(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(24.dp),
                horizontalAlignment = Alignment.CenterHorizontally,
                verticalArrangement = Arrangement.Center
            ) {
                Text(
                    text = "Rundenergebnis",
                    style = MaterialTheme.typography.headlineMedium,
                    fontWeight = FontWeight.Bold
                )

                Spacer(modifier = Modifier.height(24.dp))

                Text(
                    text = "${guessEntity.score} Punkte",
                    style = MaterialTheme.typography.displayMedium,
                    color = MaterialTheme.colorScheme.primary,
                    fontWeight = FontWeight.Bold
                )

                Spacer(modifier = Modifier.height(16.dp))

                Text(
                    text = "Entfernung: ${"%.1f".format(guessEntity.distance)} km",
                    style = MaterialTheme.typography.titleMedium
                )

                Spacer(modifier = Modifier.height(32.dp))

                Button(
                    onClick = onNextRound,
                    modifier = Modifier.fillMaxWidth()
                ) {
                    Text("Weiter")
                }
            }
        }
    }
}

